import React from "react"
import * as ReactDOMClient from "react-dom/client"







class Header extends React.Component{//класс компонента
    render(){
      return(
        <header>{this.props.title}</header>
      )  
    }
}


class App extends React.Component {




    render() {
        const Text = "Валерчик";
        return (
            <div className="name">
                <Header title = "Завтра ехать в Могилев"/>
                <Header title = " ехать в Могилев"/>
                <Header title = " в Могилев"/>
                <h5>Куршук Валерий</h5>
                <input placeholder={Text}
                    onClick={this.inputClick}
                    onMouseEnter={this.mouseOver} />
                <p>{this.Text === "Валерчи" ? "Yes" : "No"}</p>

            </div>

        );

    }   
    inputClick() { console.log("Click")}

    mouseOver() { console.log("Mouse Over")}
}


const app = ReactDOMClient.createRoot(document.getElementById("app"))

app.render(<App />)
