        return(<div className = "name">
                <h5>Куршук Валерий</h5>
        <input placeholder = {Text}
            onClick = {inputClick} 
            onMouseEnter = {mouseOver}/>
            <p>{Text === "Валерчи" ? "Yes" : "No"}</p>
            <Header />
        </div>
        )
    }

    inputClick () {console.log("Click")}
