import React from "react"

class Header extends React.Component{//класс компонента
    render(){
      return(
        <header>{this.props.curic}</header>
      )  
    }
}


class App extends React.Component {

    render() {
        const Text = "Валерчик";
        return (
            <div className="name">
                <Header curic = "Завтра ехать в Могилев"/>
                <Header curic = " ехать в Могилев"/>
                <Header curic = " в Могилев"/>
                <h5>Куршук Валерий</h5>
                <input placeholder={Text}
                    onClick={this.inputClick}
                    onMouseEnter={this.mouseOver} />
                <p>{this.Text === "Валерчи" ? "Yes" : "No"}</p>

            </div>

        );

    }   
    inputClick() { console.log("Click")}

    mouseOver() { console.log("Mouse Over")}
}

export default qwer
